sp_widget.client_script:
function BarcodeScannerCtrl ($scope, spUtil, $http) {

	var c = this;

	var varName = $scope.$parent.$parent.field.name;
	var g_form = $scope.page.g_form;
	var userId = $scope.user.sys_id;

	function handleChange (r) {
		if (r.data.changes.indexOf('x_snc_bsmc_code') !== -1) {
			$http.get('/api/now/table/sys_user', {
				params: {
					sysparm_query: 'sys_id=' + userId,
					sysparm_limit: 1,
					sysparm_fields: 'sys_id,x_snc_bsmc_code,x_snc_bsmc_guid'
				}
			}).then(function (r) {
				if (r.data.result[0].x_snc_bsmc_guid === c.data.scanner.guid) {
					g_form.setValue(varName, r.data.result[0].x_snc_bsmc_code);
				}
			});
		}
	}

	spUtil.recordWatch($scope, "sys_user", "sys_id=" + userId, handleChange);
}

sp_widget.css:
.x-snc-bsmc-area {
  background-color: #f3f3f3;
  padding: 10px;
  text-align: right;
  border-radius: 4px;
  margin-right: 10px;
}

img {
  max-width: 200px;
}

sp_widget.script:
(function BarcodeScannerServer() {
	var helper = new BarcodeScanHelper();
	data.scanner = helper.prepareData();
})();
