sys_script_include.script:
var BarcodeScanHelper = Class.create();
BarcodeScanHelper.prototype = {
	initialize: function initialize () {},

	prepareData: function getDeepLink () {
		var dlg = new global.MobileDeepLinkGenerator("request");
		var data = _prepareScanState();
		data.link = deepLinkGenerator.getFormScreenLink(
			BarcodeScanConstants.FORM_SCREEN_SYS_ID,
			data.table,
			data.sysId);
		return data;

		function _prepareScanState () {
			var gr = new GlideRecord(BarcodeScanConstants.TABLE);
			gr.get(gs.getUserID());

			var guid = gs.generateGUID();

			var data = JSON.parse(gr.getValue(BarcodeScanConstants.FIELD));
			data.guid = guid;
			gr.setValue(BarcodeScanConstants.FIELD, JSON.parse(data));
			gr.update();

			return data;
		}
	},

	type: 'BarcodeScanHelper'
};
